    "sonnet-4-5": "claude-sonnet-4-5",
    "haiku-4-5": "claude-haiku-4-5",
}

# SDK effort levels exposed through the provider interface
EffortLevel = Literal["low", "medium", "high", "max"]

# SDK thinking modes
ThinkingMode = Literal["adaptive", "enabled", "disabled"]


